function extractJson(text) {
  // Remove code block formatting if present
  let cleaned = text.trim();
  if (cleaned.startsWith("```json")) {
    cleaned = cleaned.replace(/^```json/, '').replace(/```$/, '').trim();
  } else if (cleaned.startsWith("```")) {
    cleaned = cleaned.replace(/^```/, '').replace(/```$/, '').trim();
  }
  return JSON.parse(cleaned);
}

const results = [];

for (const item of $input.all()) {
  const parsed = extractJson(item.json.text);
  results.push({ json: parsed });
}

return results;

// Group changelog items by type
const groups = {
  Added: [],
  Changed: [],
  Fixed: [],
  "Security Fixes": []
};

for (const item of items.map(i => i.json)) {
  if (groups[item.type]) {
    groups[item.type].push(item);
  }
}

// Helper to generate HTML list for each type
function buildList(items) {
  if (!items.length) return "";
  return `<ul>\n${items.map(item => 
    `<li>\n<details>\n<summary>${item.title}</summary>\n\n${item.summary}\n</details>\n</li>`
  ).join('\n')}\n</ul>`;
}

let output = "#### Changelog {#Changelog-vX.Y.Z}\n\n";

output += "##### Added\n\n";
output += buildList(groups.Added) || "<ul></ul>\n\n";

output += "##### Changed\n\n";
output += buildList(groups.Changed) || "<ul></ul>\n\n";

output += "##### Fixed\n\n";
output += buildList(groups.Fixed) || "<ul></ul>\n\n";

output += "##### Security Fixes\n\n";
output += buildList(groups["Security Fixes"]) || "-\n\n";

// Return as one markdown string
return [{ json: { changelog: output } }];

