let tokens = [];

for (const item of $input.all()) {
  tokens.push(item.json)
};

// Your grouping function
function groupByHeadings(tokens) {
  const result = [];
  let currentGroup = null;

  for (const token of tokens) {
    if (token.type === 'heading') {
      // Start a new group
      if (currentGroup) {
        result.push(currentGroup);
      }
      currentGroup = token.raw;
    } else {
      // If we're inside a heading group, keep appending
      if (currentGroup !== null) {
        currentGroup += token.raw || '';
      }
    }
  }

  // Push the last group if it exists
  if (currentGroup) {
    result.push(currentGroup);
  }

  return result;
}

// Call the function
const groups = groupByHeadings(tokens);

// Output for n8n: return array of objects with {json: ...} and incremental id
return groups.map((g, index) => ({
  json: {
    id: index + 1,  // 1-based index
    group: g
  }
}));

